mapper.update:
UPDATE WEBEMPINFO
SET dept_code = #{newDeptCode}
WHERE emp_code IN

    #{empCode}

UPDATE WEBEMPINFO
SET dept_code = #{deptCode}
WHERE emp_code IN

    #{empCode}

UPDATE WEBEMPINFO
SET work_pattern_code = #{patternCode}
WHERE dept_code = #{deptCode}

mapper.select:
SELECT
    emp_code
FROM WEBEMPINFO
WHERE work_pattern_code = #{patternName}
ORDER BY emp_code;

SELECT P.EMP_CODE AS empCode, I.EMP_NAME AS name, PI.POSITION_NAME AS position, D.DEPT_NAME AS department, S.SHIFT_NAME AS planShiftType, CAST(STUFF(S.WORK_ON_TIME, 3, 0, ':') + ':00' AS TIME) AS plannedStartTime, CAST(STUFF(S.WORK_OFF_TIME, 3, 0, ':') + ':00' AS TIME) AS plannedEndTime, #{attType} AS attType, R.REQUEST_ID AS requestId, R.REQ_REASON AS reason, R.REQ_REASON_DETAIL AS reasonDetail, R.REQ_STATUS AS status, R.APPLICANT AS applicant, G.START_NEXT_DAY AS startNextDay, G.END_NEXT_DAY AS endNextDay, S.WORK_ON_NEXT_DAY AS plannedStartNextDay, S.WORK_OFF_NEXT_DAY AS plannedEndNextDay, G.START_TIME AS startTime, G.END_TIME AS endTime, G.HALF_TYPE AS halfType FROM WEBEMPPLAN P INNER JOIN WEBEMPINFO I ON P.EMP_CODE = I.EMP_CODE INNER JOIN WEBDEPTINFO D ON P.DEPT_CODE = D.DEPT_CODE INNER JOIN WEBPOSITIONINFO PI ON I.POSITION_CODE = PI.POSITION_CODE INNER JOIN WEBSHIFTTYPE S ON P.SHIFT_CODE = S.SHIFT_CODE LEFT JOIN WEBATTREQUEST R ON R.EMP_CODE = P.EMP_CODE AND R.REQUEST_DATE = P.WORK_DATE AND R.ATTENDANCE_TYPE = #{attType} AND R.REQ_STATUS IN ('SAVED', 'REQUESTED') LEFT JOIN WEBATTREQUESTGENERAL G ON G.REQUEST_ID = R.REQUEST_ID WHERE 1=1
AND (P.HOLIDAY_YN = 'Y' AND I.STATUS != 'OFF')
AND P.HOLIDAY_YN = 'N' AND I.STATUS != 'OFF'
AND P.WORK_DATE = #{workDate}
AND P.EMP_CODE = #{empCode}
AND D.DEPT_NAME = #{deptName}
ORDER BY PI.POSITION_NAME, P.EMP_CODE, I.EMP_NAME
